mapper.select:
SELECT ud.dep_id deptId, d.depart_name departName, d.parent_id parentId, u.* FROM sys_user u LEFT JOIN sys_user_depart ud ON ud.user_id = u.id LEFT JOIN sys_depart d ON d.id = ud.dep_id
1 = 1
and d.depart_name like "%"#{user.departName}"%"
and u.username like "%"#{user.username}"%"
and u.sex = #{user.sex}
and u.email like "%"#{user.email}"%"
and u.status = #{user.status}
ORDER BY ud.dep_id + 0
desc
LIMIT #{start},#{pageSize}

select * from sys_user where del_flag = '0' and id in (select user_id from sys_user_depart where
dep_id=#{departId})

    and username = #{username}

SELECT count(*) FROM sys_user u LEFT JOIN sys_user_depart ud ON ud.user_id = u.id LEFT JOIN sys_depart d ON d.id = ud.dep_id
1 = 1
and u.username = #{user.username}
and d.depart_name = #{user.departName}

SELECT ud.dep_id deptId, d.depart_name departName, d.parent_id parentId, u.* FROM sys_user u LEFT JOIN sys_user_depart ud ON ud.user_id = u.id LEFT JOIN sys_depart d ON d.id = ud.dep_id
1 = 1
and u.username = #{user.username}
and d.depart_name = #{user.departName}
ORDER BY CAST(dep_id AS signed) LIMIT #{start},#{pageSize}

select * from sys_user where 1 = 1

    and username like "%"#{user.username}"%"

limit #{pageNo}, #{pageSize}
