     i_is_leader = 1 and i_is_state = 0


        and s_address = #{oaCalendar.sAddress}


        and s_create_by = #{oaCalendar.sCreateBy}


        and s_title like CONCAT(CONCAT('%', #{oaCalendar.sTitle}), '%')            

        and i_open_type = #{oaCalendar.iOpenType}


