        and !(DATE_FORMAT(d_end_time,'%Y-%m-%d') < #{oaCalendar.Strdate}


        or DATE_FORMAT(d_start_time,'%Y-%m-%d') > #{oaCalendar.Enddate}
        )

    and SUBSTRING_INDEX(s_user_names,',',1) = s.username

order by i_is_top desc ,s.show_order desc  ,d_create_time desc limit
${pageNo},${pageSize}

