        and s_create_by = #{oaCalendar.sCreateBy}


        and i_open_type = #{oaCalendar.iOpenType}


        and  (DATE_FORMAT(d_start_time,'%Y-%m-%d')  <=  #{oaCalendar.date} and DATE_FORMAT(d_end_time,'%Y-%m-%d') >= #{oaCalendar.date})


        and !(DATE_FORMAT(d_end_time,'%Y-%m-%d') < #{oaCalendar.Strdate}


        or DATE_FORMAT(d_start_time,'%Y-%m-%d') > #{oaCalendar.Enddate}
        )

