from ( SELECT t.ID_ id, t.NAME_ NAME, t.EXECUTION_ID_ executionId, t.PROC_INST_ID_ processInstanceId, t.PROC_DEF_ID_ processDefinitionId, t.PARENT_TASK_ID_ parentTaskId, t.DESCRIPTION_ description, t.TASK_DEF_KEY_ taskDefinitionKey, t.OWNER_ OWNER, t.start_time_ createTime, t.ASSIGNEE_ assignee, t.DUE_DATE_ dueDate, t.CATEGORY_ category, t.TENANT_ID_ tenantId, t.FORM_KEY_ formKey, v.text_ busMsg, IFNULL(t.DELETE_REASON_,'todo') del FROM act_hi_taskinst t LEFT JOIN act_hi_varinst v ON t.PROC_INST_ID_ = v.PROC_INST_ID_ AND v.name_ = 'busMsg' WHERE EXISTS(select * from act_ru_execution where PROC_INST_ID_=t.PROC_INST_ID_) AND v.name_ = 'busMsg'
and t.ASSIGNEE_ = #{pojo.userId}
UNION SELECT t.ID_ id, t.NAME_ NAME, t.EXECUTION_ID_ executionId, t.PROC_INST_ID_ processInstanceId, t.PROC_DEF_ID_ processDefinitionId, t.PARENT_TASK_ID_ parentTaskId, t.DESCRIPTION_ description, t.TASK_DEF_KEY_ taskDefinitionKey, t.OWNER_ OWNER, t.create_time_ createTime, t.ASSIGNEE_ assignee, t.DUE_DATE_ dueDate, t.CATEGORY_ category, t.TENANT_ID_ tenantId, t.FORM_KEY_ formKey, v.text_ busMsg, 'todo' del FROM act_ru_task t LEFT JOIN act_ru_identitylink i ON t.id_ = i.task_id_ LEFT JOIN act_hi_varinst v ON t.PROC_INST_ID_ = v.PROC_INST_ID_ WHERE v.name_ = 'busMsg'
