AND i.TYPE_ = 'candidate' AND t.ASSIGNEE_ IS NULL
and SUBSTRING_INDEX(v.TEXT_,',',1)=#{urgencyDegree}
) task where 1=1
and task.startTime >= #{pojo.startQueryTime}
and task.startTime <= #{pojo.endQueryTime}
and task.busMsg like CONCAT('%[%',#{pojo.dataTitle},'%]%')
