s.executionId, s.processInstanceId, s.processDefinitionId, s.parentTaskId, s.description, GROUP_CONCAT( s.taskDefinitionKey) taskDefinitionKey, s.OWNER, s.assignee, s.delegation, MAX(s.createTime) createTime, s.dueDate, s.category, s.suspensionState, s.tenantId, s.formKey, s.busMsg FROM ( SELECT t.ID_ id, t.NAME_ NAME, t.EXECUTION_ID_ executionId, t.PROC_INST_ID_ processInstanceId, t.PROC_DEF_ID_ processDefinitionId, t.PARENT_TASK_ID_ parentTaskId, t.DESCRIPTION_ description, t.TASK_DEF_KEY_ taskDefinitionKey, t.OWNER_ OWNER, t.ASSIGNEE_ assignee, t.DELEGATION_ delegation, t.CREATE_TIME_ createTime, t.DUE_DATE_ dueDate, t.CATEGORY_ category, t.SUSPENSION_STATE_ suspensionState, t.TENANT_ID_ tenantId, t.FORM_KEY_ formKey, v.text_ busMsg FROM `act_ru_task` t LEFT JOIN act_hi_varinst v ON t.PROC_INST_ID_ = v.PROC_INST_ID_ AND v.name_ = 'busMsg' WHERE ASSIGNEE_=#{pojo.userId}
and t.CREATE_TIME_ >= #{pojo.startQueryTime}
and t.CREATE_TIME_ <= #{pojo.endQueryTime}
and v.text_ like CONCAT('%[%',#{pojo.dataTitle},'%]%')
and v.text_ like CONCAT('%@mainDept:%',#{pojo.mainDept},'%@%')
and v.text_ like CONCAT('%{file_num:%',#{pojo.fileNum},'%}%')
and v.text_ like CONCAT('%$create_name:%',#{pojo.createName},'%$%')
and SUBSTRING_INDEX(v.TEXT_,',',1)=#{urgencyDegree}
UNION SELECT t.ID_ id, t.NAME_ NAME, t.EXECUTION_ID_ executionId, t.PROC_INST_ID_ processInstanceId, t.PROC_DEF_ID_ processDefinitionId, t.PARENT_TASK_ID_ parentTaskId, t.DESCRIPTION_ description, t.TASK_DEF_KEY_ taskDefinitionKey, t.OWNER_ OWNER, i.user_id_ assignee, t.DELEGATION_ delegation, t.CREATE_TIME_ createTime, t.DUE_DATE_ dueDate, t.CATEGORY_ category, t.SUSPENSION_STATE_ suspensionState, t.TENANT_ID_ tenantId, t.FORM_KEY_ formKey, v.text_ busMsg FROM act_ru_identitylink i LEFT JOIN act_ru_task t ON t.id_ = i.task_id_ LEFT JOIN act_hi_varinst v ON t.PROC_INST_ID_ = v.PROC_INST_ID_ WHERE v.name_ = 'busMsg' AND i.user_id_=#{pojo.userId} AND i.TYPE_ = 'candidate' and t.ASSIGNEE_ is null
and t.CREATE_TIME_ >= #{pojo.startQueryTime}
and t.CREATE_TIME_ <= #{pojo.endQueryTime}
and v.text_ like CONCAT('%[%',#{pojo.dataTitle},'%]%')
and v.text_ like CONCAT('%@mainDept:%',#{pojo.mainDept},'%@%')
and v.text_ like CONCAT('%{file_num:%',#{pojo.fileNum},'%}%')
and v.text_ like CONCAT('%$create_name:%',#{pojo.createName},'%$%')
and SUBSTRING_INDEX(v.TEXT_,',',1)=#{urgencyDegree}
) s LEFT JOIN oa_task_dept td ON td.proc_inst_id = s.processInstanceId AND td.task_id = s.id AND td.task_def_key = s.taskDefinitionKey AND td.type = #{type} AND td.user_id = #{pojo.userId} WHERE td.proc_inst_id = s.processInstanceId AND td.task_id = s.id AND td.task_def_key = s.taskDefinitionKey AND td.type = #{type} AND td.user_id = #{pojo.userId}
GROUP BY s.processInstanceId
order by
s.createTime,
s.createTime desc,
s.busMsg,s.createTime desc,
1=1
limit #{pageNo},#{pageSize}

