select COUNT(DISTINCT l.processInstanceId )
from ( SELECT t.ID_ id, t.NAME_ NAME, t.EXECUTION_ID_ executionId, t.PROC_INST_ID_ processInstanceId, t.PROC_DEF_ID_ processDefinitionId, t.PARENT_TASK_ID_ parentTaskId, t.DESCRIPTION_ description, t.TASK_DEF_KEY_ taskDefinitionKey, t.OWNER_ OWNER, t.start_time_ createTime, t.ASSIGNEE_ assignee, t.DUE_DATE_ dueDate, t.CATEGORY_ category, t.TENANT_ID_ tenantId, t.FORM_KEY_ formKey, v.text_ busMsg, IFNULL(t.DELETE_REASON_,'todo') del FROM act_hi_taskinst t LEFT JOIN act_hi_varinst v ON t.PROC_INST_ID_ = v.PROC_INST_ID_ AND v.name_ = 'busMsg' WHERE EXISTS(select * from act_ru_execution where PROC_INST_ID_=t.PROC_INST_ID_) AND v.name_ = 'busMsg'
and t.ASSIGNEE_ = #{pojo.userId}
UNION SELECT t.ID_ id, t.NAME_ NAME, t.EXECUTION_ID_ executionId, t.PROC_INST_ID_ processInstanceId, t.PROC_DEF_ID_ processDefinitionId, t.PARENT_TASK_ID_ parentTaskId, t.DESCRIPTION_ description, t.TASK_DEF_KEY_ taskDefinitionKey, t.OWNER_ OWNER, t.create_time_ createTime, t.ASSIGNEE_ assignee, t.DUE_DATE_ dueDate, t.CATEGORY_ category, t.TENANT_ID_ tenantId, t.FORM_KEY_ formKey, v.text_ busMsg, 'todo' del FROM act_ru_task t LEFT JOIN act_ru_identitylink i ON t.id_ = i.task_id_ LEFT JOIN act_hi_varinst v ON t.PROC_INST_ID_ = v.PROC_INST_ID_ WHERE v.name_ = 'busMsg'
AND i.user_id_ = #{pojo.userId}
AND i.user_id_ = #{pojo.userId} AND i.TYPE_ = 'candidate' AND t.ASSIGNEE_ IS NULL
) l left JOIN oa_task_dept td on td.proc_inst_id =l.processInstanceId and td.task_id = l.id and td.task_def_key=l.taskDefinitionKey and td.type=#{type}
and td.user_id= #{pojo.userId}
where td.proc_inst_id =l.processInstanceId and td.task_id = l.id and td.task_def_key=l.taskDefinitionKey and td.type=#{type}
and td.user_id= #{pojo.userId}
and l.createTime >= #{pojo.startQueryTime}
and l.createTime <= #{pojo.endQueryTime}
and l.busMsg like CONCAT('%[%',#{pojo.dataTitle},'%]%')
and l.busMsg like CONCAT('%@mainDept:%',#{pojo.mainDept},'%@%')
and l.busMsg like CONCAT('%{file_num:%',#{pojo.fileNum},'%}%')
and l.busMsg like CONCAT('%$create_name:%',#{pojo.createName},'%$%')
and SUBSTRING_INDEX(v.TEXT_,',',1)=#{urgencyDegree}

select
ht.id id, ht.NAME NAME, ht.NAME allNames, ht.executionId, ht.processInstanceId, ht.processDefinitionId, ht.parentTaskId, ht.description, ht.taskDefinitionKey taskDefinitionKey, ht.OWNER, ht.createTime createTime, ht.assignee, ht.dueDate, ht.category, ht.tenantId, ht.formKey, l.busMsg from ( SELECT * FROM ( SELECT t.ID_ id, t.NAME_ NAME, t.EXECUTION_ID_ executionId, t.PROC_INST_ID_ processInstanceId, t.PROC_DEF_ID_ processDefinitionId, t.PARENT_TASK_ID_ parentTaskId, t.DESCRIPTION_ description, t.TASK_DEF_KEY_ taskDefinitionKey, t.OWNER_ OWNER, t.start_time_ createTime, t.ASSIGNEE_ assignee, t.DUE_DATE_ dueDate, t.CATEGORY_ category, t.TENANT_ID_ tenantId, t.FORM_KEY_ formKey, v.text_ busMsg, ifNULL(t.DELETE_REASON_,'todo') del FROM act_hi_taskinst t LEFT JOIN act_hi_varinst v ON t.PROC_INST_ID_ = v.PROC_INST_ID_ AND v.name_ = 'busMsg' WHERE EXISTS(select * from act_ru_execution where PROC_INST_ID_=t.PROC_INST_ID_) AND v.name_ = 'busMsg'
and t.PROC_INST_ID_ in ( SELECT DISTINCT proc_inst_id_ FROM act_hi_taskinst where ASSIGNEE_ = #{pojo.userId} )
UNION SELECT t.ID_ id, t.NAME_ NAME, t.EXECUTION_ID_ executionId, t.PROC_INST_ID_ processInstanceId, t.PROC_DEF_ID_ processDefinitionId, t.PARENT_TASK_ID_ parentTaskId, t.DESCRIPTION_ description, t.TASK_DEF_KEY_ taskDefinitionKey, t.OWNER_ OWNER, t.create_time_ createTime, t.ASSIGNEE_ assignee, t.DUE_DATE_ dueDate, t.CATEGORY_ category, t.TENANT_ID_ tenantId, t.FORM_KEY_ formKey, v.text_ busMsg, 'todo' del FROM act_ru_task t LEFT JOIN act_ru_identitylink i ON t.id_ = i.task_id_ LEFT JOIN act_hi_varinst v ON t.PROC_INST_ID_ = v.PROC_INST_ID_ WHERE v.name_ = 'busMsg'
and t.proc_inst_id_ in ( SELECT DISTINCT proc_inst_id_ FROM act_ru_identitylink where user_id_ = #{pojo.userId} )
AND i.TYPE_ = 'candidate' AND t.ASSIGNEE_ IS NULL
) l left JOIN oa_task_dept td on td.proc_inst_id =l.processInstanceId LEFT JOIN ( SELECT * FROM ( SELECT ID_ id, NAME_ NAME, EXECUTION_ID_ executionId, PROC_INST_ID_ processInstanceId, PROC_DEF_ID_ processDefinitionId, PARENT_TASK_ID_ parentTaskId, DESCRIPTION_ description, TASK_DEF_KEY_ taskDefinitionKey, OWNER_ OWNER, start_time_ createTime, ASSIGNEE_ assignee, end_time_ dueDate, CATEGORY_ category, TENANT_ID_ tenantId, FORM_KEY_ formKey
FROM act_hi_taskinst order by CONVERT(id_,SIGNED) desc limit 999999999 ) tt GROUP BY tt.processInstanceId
) ht on ht.processInstanceId=l.processInstanceId and td.task_id = l.id and td.task_def_key=l.taskDefinitionKey and td.type= #{type}
and td.user_id= #{pojo.userId}
where td.proc_inst_id =l.processInstanceId and td.task_id = l.id and td.task_def_key=l.taskDefinitionKey and ht.processInstanceId=l.processInstanceId and td.type= #{type}
and td.user_id= #{pojo.userId}
and l.createTime >= #{pojo.startQueryTime}
and l.createTime <= #{pojo.endQueryTime}
and l.busMsg like CONCAT('%[%',#{pojo.dataTitle},'%]%')
and l.busMsg like CONCAT('%@mainDept:%',#{pojo.mainDept},'%@%')
and l.busMsg like CONCAT('%{file_num:%',#{pojo.fileNum},'%}%')
and l.busMsg like CONCAT('%$create_name:%',#{pojo.createName},'%$%')
and SUBSTRING_INDEX(v.TEXT_,',',1)=#{urgencyDegree}
GROUP BY l.processInstanceId
order by
l.createTime,
l.createTime desc,
l.busMsg,l.createTime desc,
1=1
limit #{pageNo},#{pageSize}
